package com.scoreapp.repository; import com.scoreapp.model.User; import java.util.*; public class InMemoryUserRepository implements UserRepository { private final Map<String,User> byEmail=new HashMap<>(); public void save(User u){ byEmail.put(u.getEmail(),u);} public Optional<User> findByEmail(String email){ return Optional.ofNullable(byEmail.get(email)); } public List<User> listAll(){ return new ArrayList<>(byEmail.values()); } }
